mapper.insert:
INSERT INTO board
	SET type = #{type}
	, title = #{title}
	, body = #{body}
	, memberId = #{memberId}
	, regDate = NOW()
	, updateDate = NOW()

	select max(id) AS id from board

mapper.update:
UPDATE board
	SET type = #{type}
	, title = #{title}
	, body = #{body}
	, updateDate = NOW()
WHERE id = #{id}
